mapper.select:
WITH RECURSIVE case_tree AS ( SELECT id, parent_id, depth, uid, gmt_create, gmt_modified, name, biz_extension, extension, tags, is_deleted FROM case_test_case WHERE id = #{rootId} AND is_deleted = 0
UNION ALL
SELECT c.id, c.parent_id, c.depth, c.uid, c.gmt_create, c.gmt_modified, c.name, c.biz_extension, c.extension, c.tags, c.is_deleted FROM case_test_case c INNER JOIN case_tree ct ON c.parent_id = ct.id WHERE c.is_deleted = 0 ) SELECT * FROM case_tree;

mapper.update:
UPDATE case_test_case a JOIN (
SELECT #{item.id} as id, #{item.uid} as uid, #{item.parentUid} as parent_uid, #{item.depth} as depth, #{item.gmtCreate} as gmt_create, #{item.gmtModified} as gmt_modified, #{item.name} as name, #{item.bizExtension} as biz_extension, #{item.extension} as extension, #{item.tags} as tags, #{item.projectId} as project_id, #{item.isDeleted} as is_deleted
) b USING (id) SET a.parent_uid = b.parent_uid, a.depth = b.depth, a.gmt_modified = now(), a.name = b.name, a.biz_extension = b.biz_extension, a.extension = b.extension, a.tags = b.tags, a.project_id = b.project_id, a.is_deleted = b.is_deleted
